def calculate_refunded_amount(plan):
    try:
        end_date = datetime.strptime(plan['end_date'], "%d.%m.%Y –≤ %H:%M").replace(tzinfo=pytz.UTC)
        start_date = datetime.strptime(plan['start_date'], "%d.%m.%Y –≤ %H:%M").replace(tzinfo=pytz.UTC)
        now = datetime.now(pytz.UTC)

        if now >= end_date:
            return 0.0, "–ü–æ–¥–ø–∏—Å–∫–∞ —É–∂–µ –∏—Å—Ç–µ–∫–ª–∞"

        if now < start_date:
            refund_amount = plan['price']
            final_refunded = refund_amount * (1 - REFUND_COMMISSION)
            return round(final_refunded, 2), None

        total_days = (end_date - start_date).days
        if total_days <= 0:
            return 0.0, "–û—à–∏–±–∫–∞: –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω–∞—è –¥–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å –ø–æ–¥–ø–∏—Å–∫–∏"

        remaining_time = end_date - now
        remaining_days = max(0, min(total_days, remaining_time.total_seconds() / (24 * 3600)))
        daily_cost = plan['price'] / total_days
        refund_amount = daily_cost * remaining_days
        refund_amount = min(refund_amount, plan['price'])
        final_refunded = refund_amount * (1 - REFUND_COMMISSION)
        final_refunded = round(final_refunded, 2)

        if final_refunded < MIN_REFUND_AMOUNT:
            return 0.0, "–°—É–º–º–∞ –≤–æ–∑–≤—Ä–∞—Ç–∞ —Å–ª–∏—à–∫–æ–º –º–∞–ª–∞"

        return final_refunded, None
    except Exception as e:
        return 0.0, f"–û—à–∏–±–∫–∞ —Ä–∞—Å—á–µ—Ç–∞ –≤–æ–∑–≤—Ä–∞—Ç–∞: {str(e)}"

def refund_payment(user_id, refund_amount, payment_id, plan):

    refund_data = {
        "paymentId": payment_id,
        "amount": {
            "value": f"{refund_amount:.2f}",
            "currency": "RUB"
        }
    }

    headers = {
        "Content-Type": "application/json",
        "Authorization": f"Bearer {PAYMASTER_TOKEN}",
        "Idempotency-Key": f"{user_id}_{int(datetime.now(pytz.UTC).timestamp())}"
    }

    try:
        response = requests.post(f"{PAYMASTER_API_URL}refunds", json=refund_data, headers=headers)
        print(f"Paymaster request: {refund_data}")
        print(f"Paymaster response: {response.status_code} - {response.text}")

        if response.status_code == 200:
            result = response.json()
            if result.get("status") == "Pending" or result.get("id"):
                return True
            raise Exception(f"Paymaster: {result.get('message', '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞')}")
        raise Exception(f"Paymaster HTTP {response.status_code}: {response.text}")

    except Exception as e:
        data = load_payment_data()
        refund_data = {
            "user_id": user_id,
            "plan_name": plan["plan_name"],
            "refund_amount": refund_amount,
            "refund_date": datetime.now(pytz.UTC).strftime("%d.%m.%Y –≤ %H:%M"),
            "telegram_payment_charge_id": plan.get("telegram_payment_charge_id", ""),
            "provider_payment_charge_id": payment_id,
            "status": "failed"
        }
        data["refunds"].append(refund_data)
        save_payments_data(data)

        admin_chat_id = load_admin_chat_id()
        if admin_chat_id:
            bot.send_message(
                admin_chat_id,
                f"–û—à–∏–±–∫–∞ –≤–æ–∑–≤—Ä–∞—Ç–∞ –¥–ª—è user_id={user_id}: {str(e)}\n"
                f"Payment ID: {payment_id}\n"
                f"Request: {refund_data}\n"
                f"Response: {response.text if 'response' in locals() else 'No response'}"
            )
        print(f"–û—à–∏–±–∫–∞ –≤–æ–∑–≤—Ä–∞—Ç–∞ –¥–ª—è user_id={user_id}: {str(e)}")
        return False

def confirm_cancellation(message, user_id, paid_plans):

    if message.text == "–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø–æ–¥–ø–∏—Å–∫—É":
        payments_function(message, show_description=False)
        return
    if message.text == "–í –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é":
        return_to_menu(message)
        return

    try:
        subscription_numbers = [int(num.strip()) for num in message.text.split(',')]
        valid_numbers = [num for num in subscription_numbers if 1 <= num <= len(paid_plans)]
        invalid_numbers = [num for num in subscription_numbers if num not in valid_numbers]

        if not valid_numbers:
            markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
            markup.add("–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø–æ–¥–ø–∏—Å–∫—É")
            markup.add("–í –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é")
            bot.send_message(user_id, "‚ö†Ô∏è –í—Å–µ –Ω–æ–º–µ—Ä–∞ –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã!\n–í–≤–µ–¥–∏—Ç–µ –Ω–æ–º–µ—Ä–∞ —á–µ—Ä–µ–∑ –∑–∞–ø—è—Ç—É—é:", reply_markup=markup, parse_mode="Markdown")
            bot.register_next_step_handler(message, confirm_cancellation, user_id, paid_plans)
            return

        if invalid_numbers:
            bot.send_message(user_id, f"‚ö†Ô∏è –ù–æ–º–µ—Ä–∞ `{invalid_numbers}` –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã –∏ –ø—Ä–æ–ø—É—â–µ–Ω—ã!", parse_mode="Markdown")

        refund_summary = "*–ü–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ –æ—Ç–º–µ–Ω—ã:*\n\n"
        total_refunded = 0.0
        for num in valid_numbers:
            plan = paid_plans[num - 1]
            refund_amount, error = calculate_refunded_amount(plan)
            if error:
                refund_summary += f"‚ùå –ü–æ–¥–ø–∏—Å–∫–∞ ‚Ññ{num}: {error}\n"
                continue
            refund_summary += (
                f"üí≥ –ü–æ–¥–ø–∏—Å–∫–∞ ‚Ññ{num} ({plan['plan_name'].capitalize()}):\n"
                f"üí∏ –í–æ–∑–≤—Ä–∞—Ç: {refund_amount:.2f} —Ä—É–±.\n\n"
            )
            total_refunded += refund_amount

        if total_refunded == 0:
            bot.send_message(user_id, "‚ùå –ù–µ—Ç –ø–æ–¥–ø–∏—Å–æ–∫ –¥–ª—è –≤–æ–∑–≤—Ä–∞—Ç–∞!", parse_mode="Markdown")
            payments_function(message, show_description=False)
            return

        refund_summary += f"üì• –ò—Ç–æ–≥–æ: *{total_refunded:.2f} —Ä—É–±.*"
        markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
        markup.add("–ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç—å", "–û—Ç–º–µ–Ω–∏—Ç—å")
        bot.send_message(user_id, refund_summary, parse_mode="Markdown")
        bot.send_message(user_id, "–ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç–µ –¥–µ–π—Å—Ç–≤–∏–µ:", reply_markup=markup)
        bot.register_next_step_handler(message, process_subscription_cancellation, user_id, paid_plans, valid_numbers)

    except ValueError:
        markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
        markup.add("–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø–æ–¥–ø–∏—Å–∫—É")
        markup.add("–í –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é")
        bot.send_message(user_id, "–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç! –í–≤–µ–¥–∏—Ç–µ –Ω–æ–º–µ—Ä–∞ —á–µ—Ä–µ–∑ –∑–∞–ø—è—Ç—É—é:", reply_markup=markup, parse_mode="Markdown")
        bot.register_next_step_handler(message, confirm_cancellation, user_id, paid_plans)

def process_subscription_cancellation(message, user_id, paid_plans, subscription_numbers):

    if message.text == "–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø–æ–¥–ø–∏—Å–∫—É":
        payments_function(message, show_description=False)
        return
    if message.text == "–í –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é":
        return_to_menu(message)
        return

    if message.text != "–ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç—å":
        bot.send_message(user_id, "‚ùå –î–µ–π—Å—Ç–≤–∏–µ –æ—Ç–º–µ–Ω–µ–Ω–æ!", parse_mode="Markdown")
        payments_function(message, show_description=False)
        return

    data = load_payment_data()
    user_data = data['subscriptions']['users'].get(str(user_id), {})
    total_refunded = 0.0

    for num in subscription_numbers:
        plan = paid_plans[num - 1]
        refund_amount, error = calculate_refunded_amount(plan)
        if error:
            bot.send_message(user_id, f"‚ùå –ü–æ–¥–ø–∏—Å–∫–∞ ‚Ññ{num}: {error}", parse_mode="Markdown")
            continue

        payment_id = plan.get('provider_payment_charge_id', '')
        if not payment_id.isdigit():
            bot.send_message(user_id, f"‚ùå –ü–æ–¥–ø–∏—Å–∫–∞ ‚Ññ{num}: –Ω–µ–≤–µ—Ä–Ω—ã–π ID –ø–ª–∞—Ç–µ–∂–∞!", parse_mode="Markdown")
            admin_chat_id = load_admin_chat_id()
            if admin_chat_id:
                bot.send_message(admin_chat_id, f"–û—à–∏–±–∫–∞ –≤–æ–∑–≤—Ä–∞—Ç–∞ –¥–ª—è user_id={user_id}: –Ω–µ–≤–µ—Ä–Ω—ã–π provider_payment_charge_id={payment_id}")
            continue

        success = refund_payment(user_id, refund_amount, payment_id, plan)
        if not success:
            bot.send_message(user_id, f"‚ùå –û—à–∏–±–∫–∞ –≤–æ–∑–≤—Ä–∞—Ç–∞ –¥–ª—è –ø–æ–¥–ø–∏—Å–∫–∏ ‚Ññ{num}! –û–±—Ä–∞—Ç–∏—Ç–µ—Å—å –≤ –ø–æ–¥–¥–µ—Ä–∂–∫—É...", parse_mode="Markdown")
            continue

        user_data['plans'].remove(plan)
        user_data['total_amount'] = max(0, user_data.get('total_amount', 0) - refund_amount)
        data['all_users_total_amount'] = max(0, data.get('all_users_total_amount', 0) - refund_amount)

        refund_data = {
            "user_id": user_id,
            "plan_name": plan['plan_name'],
            "refund_amount": refund_amount,
            "refund_date": datetime.now(pytz.UTC).strftime("%d.%m.%Y –≤ %H:%M"),
            "telegram_payment_charge_id": plan.get('telegram_payment_charge_id', ''),
            "provider_payment_charge_id": payment_id,
            "status": "success"
        }
        data['refunds'].append(refund_data)
        total_refunded += refund_amount

    data['subscriptions']['users'][str(user_id)] = user_data
    save_payments_data(data)

    if total_refunded > 0:
        bot.send_message(user_id, f"‚úÖ –ü–æ–¥–ø–∏—Å–∫–∏ –æ—Ç–º–µ–Ω–µ–Ω—ã! –í–æ–∑–≤—Ä–∞—â–µ–Ω–æ: *{total_refunded:.2f} —Ä—É–±.*", parse_mode="Markdown")
    else:
        bot.send_message(user_id, "‚ùå –ù–µ —É–¥–∞–ª–æ—Å—å –æ—Ç–º–µ–Ω–∏—Ç—å –ø–æ–¥–ø–∏—Å–∫–∏!", parse_mode="Markdown")

    payments_function(message, show_description=False)

