def format_time(minutes):
    if minutes < 60:
        return f"{minutes} –º–∏–Ω."
    days = minutes // 1440
    hours = (minutes % 1440) // 60
    mins = minutes % 60
    parts = []
    if days:
        parts.append(f"{days} –¥–Ω.")
    if hours:
        parts.append(f"{hours} —á.")
    if mins:
        parts.append(f"{mins} –º–∏–Ω.")
    return " ".join(parts)

@bot.message_handler(func=lambda message: message.text == "–í–∞—à–∏ –±–∞–ª–ª—ã")
def view_points(message):
    user_id = str(message.from_user.id)
    data = load_payment_data()
    history = data['subscriptions']['users'].get(user_id, {}).get('points_history', [])
    
    points = data['subscriptions']['users'].get(user_id, {}).get('referral_points', 0)
    
    total_points_earned = sum(h['points'] for h in history if h['action'] == "earned")
    total_points_spent = sum(h['points'] for h in history if h['action'] == "spent")
    
    gifted_points = sum(h['points'] for h in history if h['action'] == "spent" and "–ü–æ–¥–∞—Ä–æ–∫ @" in h['reason'])
    received_gift_points = sum(h['points'] for h in history if h['action'] == "earned" and "–ü–æ–¥–∞—Ä–æ–∫ –æ—Ç" in h['reason'])
    
    gifted_time = sum(int(re.search(r"(\d+)\s*–º–∏–Ω—É—Ç", h['reason']).group(1)) for h in history if h['action'] == "spent" and "–ü–æ–¥–∞—Ä–æ–∫ –≤—Ä–µ–º–µ–Ω–∏" in h['reason'] and re.search(r"(\d+)\s*–º–∏–Ω—É—Ç", h['reason'])) or 0
    received_gift_time = sum(int(re.search(r"(\d+)\s*–º–∏–Ω—É—Ç", h['reason']).group(1)) for h in history if h['action'] == "earned" and "–ü–æ–¥–∞—Ä–æ–∫ –≤—Ä–µ–º–µ–Ω–∏" in h['reason'] and re.search(r"(\d+)\s*–º–∏–Ω—É—Ç", h['reason'])) or 0
    
    referral_points = sum(h['points'] for h in history if h['action'] == "earned" and "–†–µ—Ñ–µ—Ä–∞–ª" in h['reason'])
    purchase_points = sum(h['points'] for h in history if h['action'] == "earned" and "–ü–æ–∫—É–ø–∫–∞" in h['reason'])
    top_referral_points = sum(h['points'] for h in history if h['action'] == "earned" and "–¢–æ–ø-10 —Ä–µ—Ñ–µ—Ä–∞–ª–æ–≤" in h['reason'])
    exchanged_time = sum(h['points'] for h in history if h['action'] == "spent" and "–û–±–º–µ–Ω –Ω–∞" in h['reason'] and ("—á." in h['reason'] or "–¥–Ω." in h['reason']))
    exchanged_discount = sum(h['points'] for h in history if h['action'] == "spent" and "–û–±–º–µ–Ω –Ω–∞" in h['reason'] and "%" in h['reason'])
    exchanged_features = sum(h['points'] for h in history if h['action'] == "spent" and "–û–±–º–µ–Ω –Ω–∞ —Ñ—É–Ω–∫—Ü–∏—é" in h['reason'])
    
    bot.send_message(message.chat.id, (
        f"*–í–∞—à–∏ –±–∞–ª–ª—ã*\n\n\n"
        f"üéÅ *–¢–µ–∫—É—â–∏–µ –±–∞–ª–ª—ã:* {points}\n\n"
        f"üî• *–í—Å–µ–≥–æ –∑–∞—Ä–∞–±–æ—Ç–∞–Ω–æ:* {total_points_earned}\n"
        f"  ‚Ä¢ –ó–∞ —Ä–µ—Ñ–µ—Ä–∞–ª–æ–≤: {referral_points}\n"
        f"  ‚Ä¢ –ó–∞ –ø–æ–∫—É–ø–∫–∏: {purchase_points}\n"
        f"  ‚Ä¢ –ó–∞ —Ç–æ–ø —Ä–µ—Ñ–µ—Ä–∞–ª–æ–≤: {top_referral_points}\n"
        f"  ‚Ä¢ –ü–æ–ª—É—á–µ–Ω–æ –≤ –ø–æ–¥–∞—Ä–æ–∫: {received_gift_points}\n\n"
        f"üí∏ *–í—Å–µ–≥–æ –ø–æ—Ç—Ä–∞—á–µ–Ω–æ:* {total_points_spent}\n"
        f"  ‚Ä¢ –ù–∞ –≤—Ä–µ–º—è: {exchanged_time}\n"
        f"  ‚Ä¢ –ù–∞ —Å–∫–∏–¥–∫–∏: {exchanged_discount}\n"
        f"  ‚Ä¢ –ù–∞ —Ñ—É–Ω–∫—Ü–∏–∏: {exchanged_features}\n"
        f"  ‚Ä¢ –ü–æ–¥–∞—Ä–µ–Ω–æ –¥—Ä—É–≥–∏–º: {gifted_points}\n\n"
        f"‚è≥ *–ü–æ–¥–∞—Ä–µ–Ω–æ –≤—Ä–µ–º–µ–Ω–∏:* {format_time(gifted_time)}\n"
        f"‚è≥ *–ü–æ–ª—É—á–µ–Ω–æ –≤—Ä–µ–º–µ–Ω–∏:* {format_time(received_gift_time)}"
    ), parse_mode="Markdown")

# ---------- 10.8. –ü–û–î–ü–ò–°–ö–ê –ù–ê –ë–û–¢–ê (–û–ë–ú–ï–ù–Ø–¢–¨ –ë–ê–õ–õ–´) ----------

@bot.message_handler(func=lambda message: message.text == "–û–±–º–µ–Ω—è—Ç—å –±–∞–ª–ª—ã")
def exchange_points_handler(message):
    user_id = str(message.from_user.id)
    data = load_payment_data()
    points = data['subscriptions']['users'].get(user_id, {}).get('referral_points', 0)
    
    if points < 0.5:
        bot.send_message(message.chat.id, "‚ùå –ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –±–∞–ª–ª–æ–≤!", parse_mode="Markdown")
        return_to_scores_menu(message)
        return
    
    has_subscription = False
    plans = data['subscriptions']['users'].get(user_id, {}).get('plans', [])
    now = datetime.now()
    for plan in plans:
        end_date = datetime.strptime(plan['end_date'], "%d.%m.%Y –≤ %H:%M")
        if end_date > now:
            has_subscription = True
            break
    
    markup = telebot.types.ReplyKeyboardMarkup(resize_keyboard=True)
    markup.add(telebot.types.KeyboardButton("–û–±–º–µ–Ω –Ω–∞ –≤—Ä–µ–º—è"), telebot.types.KeyboardButton("–û–±–º–µ–Ω –Ω–∞ —Å–∫–∏–¥–∫—É"))
    # –ò—Å–ø—Ä–∞–≤–ª–µ–Ω–æ: –∫–Ω–æ–ø–∫–∞ "–û–±–º–µ–Ω –Ω–∞ —Ñ—É–Ω–∫—Ü–∏–∏" –≤—Å–µ–≥–¥–∞ –¥–æ—Å—Ç—É–ø–Ω–∞
    markup.add(telebot.types.KeyboardButton("–û–±–º–µ–Ω –Ω–∞ —Ñ—É–Ω–∫—Ü–∏–∏"))
    markup.add(telebot.types.KeyboardButton("–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –±–∞–ª–ª—ã"))
    markup.add(telebot.types.KeyboardButton("–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø–æ–¥–ø–∏—Å–∫—É"))
    markup.add(telebot.types.KeyboardButton("–í –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é"))
    
    total_exchanged = sum(h['points'] for h in data['subscriptions']['users'].get(user_id, {}).get('points_history', []) if h['action'] == "spent")
    exchange_rate = 2.4 if total_exchanged >= 100 else 2.0
    
    bot.send_message(message.chat.id, (
        f"*–û–±–º–µ–Ω –±–∞–ª–ª–æ–≤*\n\n"
        f"üéÅ *–¢–µ–∫—É—â–∏–µ –±–∞–ª–ª—ã:* {points:.1f}\n\n"
        f"_P.S. –≤—ã –º–æ–∂–µ—Ç–µ –æ–±–º–µ–Ω—è—Ç—å –±–∞–ª–ª—ã –Ω–∞:_\n"
        f"- –í—Ä–µ–º—è –ø–æ–¥–ø–∏—Å–∫–∏: 1 –±–∞–ª–ª = {exchange_rate} —á–∞—Å–∞\n"
        f"- –°–∫–∏–¥–∫—É: 15 –±–∞–ª–ª–æ–≤ = 5% (–º–∞–∫—Å. 35%)\n"
        f"- –î–æ—Å—Ç—É–ø –∫ —Ñ—É–Ω–∫—Ü–∏—è–º: 5 –±–∞–ª–ª–æ–≤ = 15 –º–∏–Ω—É—Ç\n\n"
        "–í—ã–±–µ—Ä–∏—Ç–µ –æ–ø—Ü–∏—é:"
    ), reply_markup=markup, parse_mode="Markdown")
    bot.register_next_step_handler(message, process_exchange_option, points, exchange_rate, has_subscription)

