def process_gift_time_unit(message, recipient_id, total_available_minutes):
    if message.text == "–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –±–∞–ª–ª—ã":
        return_to_scores_menu(message)
        return
    if message.text == "–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø–æ–¥–∞—Ä–∫–∏":
        return_to_gifts_menu(message)
        return
    if message.text == "–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø–æ–¥–ø–∏—Å–∫—É":
        payments_function(message, show_description=False)
        return
    if message.text == "–í –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é":
        return_to_menu(message)
        return
    
    unit = message.text
    if unit not in ["–ú–∏–Ω—É—Ç—ã", "–ß–∞—Å—ã", "–î–Ω–∏"]:
        bot.send_message(message.chat.id, "–í—ã–±–µ—Ä–∏—Ç–µ –æ–¥–Ω—É –∏–∑ –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–Ω—ã—Ö –µ–¥–∏–Ω–∏—Ü –≤—Ä–µ–º–µ–Ω–∏!", parse_mode="Markdown")
        process_gift_time_recipient(message, total_available_minutes)
        return
    
    markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
    markup.add("–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø–æ–¥–∞—Ä–∫–∏", "–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –±–∞–ª–ª—ã")
    markup.add(types.KeyboardButton("–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø–æ–¥–ø–∏—Å–∫—É"))
    markup.add(types.KeyboardButton("–í –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é"))
    
    unit_prompt = {
        "–ú–∏–Ω—É—Ç—ã": "–º–∏–Ω—É—Ç",
        "–ß–∞—Å—ã": "—á–∞—Å–æ–≤",
        "–î–Ω–∏": "–¥–Ω–µ–π"
    }[unit]
    
    bot.send_message(message.chat.id, (
        f"üéÅ *–ü–æ–¥–∞—Ä–æ–∫ –≤—Ä–µ–º–µ–Ω–∏*\n\n"
        f"‚è≥ *–î–æ—Å—Ç—É–ø–Ω–æ:* {total_available_minutes} –º–∏–Ω—É—Ç\n\n"
        f"–í–≤–µ–¥–∏—Ç–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ {unit_prompt} –¥–ª—è –ø–æ–¥–∞—Ä–∫–∞:"
    ), reply_markup=markup, parse_mode="Markdown")
    bot.register_next_step_handler(message, process_gift_time_amount, recipient_id, total_available_minutes, unit)

def process_gift_time_amount(message, recipient_id, total_available_minutes, unit):
    if message.text == "–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –±–∞–ª–ª—ã":
        return_to_scores_menu(message)
        return
    if message.text == "–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø–æ–¥–∞—Ä–∫–∏":
        return_to_gifts_menu(message)
        return
    if message.text == "–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø–æ–¥–ø–∏—Å–∫—É":
        payments_function(message, show_description=False)
        return
    if message.text == "–í –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é":
        return_to_menu(message)
        return
    
    user_id = str(message.from_user.id)
    data = load_payment_data()
    
    try:
        amount = int(message.text)
        if amount <= 0:
            raise ValueError("‚ö†Ô∏è –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å –ø–æ–ª–æ–∂–∏—Ç–µ–ª—å–Ω—ã–º!")
        
        if unit == "–ú–∏–Ω—É—Ç—ã":
            gift_minutes = amount
        elif unit == "–ß–∞—Å—ã":
            gift_minutes = amount * 60
        elif unit == "–î–Ω–∏":
            gift_minutes = amount * 1440
        
        if gift_minutes > total_available_minutes:
            raise ValueError("‚ùå –ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –≤—Ä–µ–º–µ–Ω–∏ –¥–ª—è –ø–æ–¥–∞—Ä–∫–∞!")
        
        sender_username = data['subscriptions']['users'][user_id].get('username', '–Ω–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π')
        recipient_username = data['subscriptions']['users'][recipient_id].get('username', '–Ω–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π')
        
        gift_duration = timedelta(minutes=gift_minutes)
        remaining_minutes = gift_minutes
        
        user_plans = data['subscriptions']['users'][user_id]['plans']
        for plan in sorted(
            [p for p in user_plans if p['plan_name'] in ['weekly', 'monthly', 'yearly'] and p['source'] == 'user'],
            key=lambda x: datetime.strptime(x['end_date'], "%d.%m.%Y –≤ %H:%M")
        ):
            if remaining_minutes <= 0:
                break
            end_date = datetime.strptime(plan['end_date'], "%d.%m.%Y –≤ %H:%M")
            if end_date <= datetime.now():
                continue
            available_minutes = int((end_date - datetime.now()).total_seconds() // 60)
            minutes_to_deduct = min(remaining_minutes, available_minutes)
            new_end_date = end_date - timedelta(minutes=minutes_to_deduct)
            plan['end_date'] = new_end_date.strftime("%d.%m.%Y –≤ %H:%M")
            remaining_minutes -= minutes_to_deduct
        
        user_plans[:] = [p for p in user_plans if datetime.strptime(p['end_date'], "%d.%m.%Y –≤ %H:%M") > datetime.now()]
        
        recipient_data = data['subscriptions']['users'].setdefault(recipient_id, {
            "plans": [],
            "total_amount": 0,
            "username": recipient_username,
            "referral_points": 0,
            "free_feature_trials": {},
            "promo_usage_history": [],
            "referral_milestones": {},
            "points_history": []
        })
        
        latest_end = max(
            [datetime.strptime(p['end_date'], "%d.%m.%Y –≤ %H:%M") for p in recipient_data['plans']] or [datetime.now()]
        )
        new_end = latest_end + gift_duration
        
        recipient_data['plans'].append({
            "plan_name": "gift_time",
            "start_date": latest_end.strftime("%d.%m.%Y –≤ %H:%M"),
            "end_date": new_end.strftime("%d.%m.%Y –≤ %H:%M"),
            "price": 0,
            "source": f"gift_from_{user_id}"
        })
        
        data['subscriptions']['users'][user_id].setdefault('points_history', []).append({
            "action": "spent",
            "points": 0,
            "reason": f"–ü–æ–¥–∞—Ä–æ–∫ –≤—Ä–µ–º–µ–Ω–∏ {recipient_username}: {gift_minutes} –º–∏–Ω—É—Ç",
            "date": datetime.now().strftime("%d.%m.%Y –≤ %H:%M")
        })
        
        recipient_data.setdefault('points_history', []).append({
            "action": "earned",
            "points": 0,
            "reason": f"–ü–æ–¥–∞—Ä–æ–∫ –≤—Ä–µ–º–µ–Ω–∏ –æ—Ç {sender_username}: {gift_minutes} –º–∏–Ω—É—Ç",
            "date": datetime.now().strftime("%d.%m.%Y –≤ %H:%M")
        })
        
        save_payments_data(data)
        
        days = gift_minutes // 1440
        hours = (gift_minutes % 1440) // 60
        minutes = gift_minutes % 60
        gift_description = ""
        if days > 0:
            gift_description += f"{days} –¥–Ω. "
        if hours > 0:
            gift_description += f"{hours} —á. "
        if minutes > 0:
            gift_description += f"{minutes} –º–∏–Ω."
        
        bot.send_message(user_id, (
            f"üéâ *–í—ã –ø–æ–¥–∞—Ä–∏–ª–∏ {recipient_username} {gift_description}!*\n\n"
            f"‚è≥ –í–∞—à–∞ –ø–æ–¥–ø–∏—Å–∫–∞ —Ç–µ–ø–µ—Ä—å –∞–∫—Ç–∏–≤–Ω–∞ –¥–æ: "
            f"{min([datetime.strptime(p['end_date'], '%d.%m.%Y –≤ %H:%M') for p in user_plans] or [datetime.now()]).strftime('%d.%m.%Y –≤ %H:%M')}"
        ), parse_mode="Markdown")
        
        bot.send_message(recipient_id, (
            f"üéÅ *{sender_username} –ø–æ–¥–∞—Ä–∏–ª –≤–∞–º {gift_description}!*\n\n"
            f"‚è≥ –í–∞—à–∞ –ø–æ–¥–ø–∏—Å–∫–∞ —Ç–µ–ø–µ—Ä—å –∞–∫—Ç–∏–≤–Ω–∞ –¥–æ: {new_end.strftime('%d.%m.%Y –≤ %H:%M')}"
        ), parse_mode="Markdown")
        
        gifts_menu(message)
        
    except ValueError as e:
        markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
        markup.add("–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø–æ–¥–∞—Ä–∫–∏", "–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –±–∞–ª–ª—ã")  
        markup.add(types.KeyboardButton("–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –ø–æ–¥–ø–∏—Å–∫—É"))
        markup.add(types.KeyboardButton("–í –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é"))
        error_msg = str(e) if str(e).startswith(("–ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ", "–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ")) else "–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ–µ —á–∏—Å–ª–æ!"
        bot.send_message(message.chat.id, f"‚ùå {error_msg}", reply_markup=markup, parse_mode="Markdown")
        bot.register_next_step_handler(message, process_gift_time_amount, recipient_id, total_available_minutes, unit)

# ---------- 10.12. –ü–û–î–ü–ò–°–ö–ê –ù–ê –ë–û–¢–ê (–ò–°–¢–û–†–ò–Ø –ü–û–î–ê–†–ö–û–í) ----------

@bot.message_handler(func=lambda message: message.text == "–ò—Å—Ç–æ—Ä–∏—è –ø–æ–¥–∞—Ä–∫–æ–≤")
def view_gifts_history(message):
    user_id = str(message.from_user.id)
    data = load_payment_data()
    history = data['subscriptions']['users'].get(user_id, {}).get('points_history', [])

    if not history:
        bot.send_message(message.chat.id, "‚ùå –ò—Å—Ç–æ—Ä–∏—è –ø–æ–¥–∞—Ä–∫–æ–≤ –∏ –æ–ø–µ—Ä–∞—Ü–∏–π –ø—É—Å—Ç–∞!", parse_mode="Markdown")
        return

    msg = "*–ò—Å—Ç–æ—Ä–∏—è –ø–æ–¥–∞—Ä–∫–æ–≤ –∏ –æ–ø–µ—Ä–∞—Ü–∏–π:*\n\n\n"
    for i, entry in enumerate(history, 1):
        if "–ü–æ–¥–∞—Ä–æ–∫ –≤—Ä–µ–º–µ–Ω–∏" in entry['reason'] or "–ü–æ–¥–∞—Ä–æ–∫ @" in entry['reason']:
            action = "–ü–æ–¥–∞—Ä–µ–Ω–æ" if entry['action'] == "spent" else "–ü–æ–ª—É—á–µ–Ω–æ"
            points_or_time = f"{entry['points']} –±–∞–ª–ª–æ–≤" if entry['points'] > 0 else entry['reason'].split(': ')[-1]
            msg += f"üéÅ ‚Ññ{i}. {entry['date']} - {action}: {points_or_time} ({escape_markdown(entry['reason'])})\n\n"
        elif "–û–±–º–µ–Ω –Ω–∞" in entry['reason']:
            msg += f"üîÑ ‚Ññ{i}. {entry['date']} - –û–±–º–µ–Ω: {entry['reason'].split('–Ω–∞ ')[-1]} ({escape_markdown(entry['reason'])})\n\n"
        else:
            action = "–ó–∞—Ä–∞–±–æ—Ç–∞–Ω–æ" if entry['action'] == "earned" else "–ü–æ—Ç—Ä–∞—á–µ–Ω–æ"
            msg += f"üìù ‚Ññ{i}. {entry['date']} - {action}: {entry['points']} –±–∞–ª–ª–æ–≤ ({escape_markdown(entry['reason'])})\n\n"

    while len(msg) > 4096:
        split_index = msg.rfind('\n\n', 0, 4096)
        if split_index == -1:
            split_index = 4095
        part = msg[:split_index]
        bot.send_message(message.chat.id, part, parse_mode="Markdown")
        msg = msg[split_index:]

    bot.send_message(message.chat.id, msg, parse_mode="Markdown")

