# –ü—É—Ç–∏ –∫ —Ñ–∞–π–ª–∞–º
RASTAMOZKA_JSON_PATH = os.path.join('files', 'rastamozka.json')
USER_HISTORY_PATH = os.path.join('data base', 'calculators', 'rastamozka', 'rastamozka_users.json')

rastamozka_data = {}
user_history = {}
user_data = {}

# –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –∞–∫—Ç—É–∞–ª—å–Ω—ã—Ö –∫—É—Ä—Å–æ–≤ –≤–∞–ª—é—Ç –∏–∑ API –¶–µ–Ω—Ç—Ä–æ–±–∞–Ω–∫–∞ –†–§
def fetch_exchange_rates_cbr():
    url = 'https://www.cbr-xml-daily.ru/daily_json.js'
    try:
        response = requests.get(url)
        data = response.json()
        rates = data['Valute']
        return {
            'USD': rates['USD']['Value'],  # 1 USD = X RUB
            'EUR': rates['EUR']['Value'],  # 1 EUR = X RUB
            'BYN': rates['BYN']['Value'],  # 1 BYN = X RUB
            'CNY': rates['CNY']['Value'] / 10,  # API –¥–∞–µ—Ç –∫—É—Ä—Å –∑–∞ 10 CNY, –¥–µ–ª–∏–º –Ω–∞ 10
            'JPY': rates['JPY']['Value'] / 100,  # API –¥–∞–µ—Ç –∫—É—Ä—Å –∑–∞ 100 JPY, –¥–µ–ª–∏–º –Ω–∞ 100
            'KRW': rates['KRW']['Value'] / 1000,  # API –¥–∞–µ—Ç –∫—É—Ä—Å –∑–∞ 1000 KRW, –¥–µ–ª–∏–º –Ω–∞ 1000
            'RUB': 1  # –†—É–±–ª—å –≤—Å–µ–≥–¥–∞ 1
        }
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–ø—Ä–æ—Å–µ –∫—É—Ä—Å–æ–≤ –≤–∞–ª—é—Ç –¶–ë –†–§: {e}")
        return get_default_rates()

# –†–µ–∑–µ—Ä–≤–Ω—ã–µ –∫—É—Ä—Å—ã –Ω–∞ —Å–ª—É—á–∞–π —Å–±–æ—è API (–≤–∞—à–∏ –∏—Å—Ö–æ–¥–Ω—ã–µ –∑–Ω–∞—á–µ–Ω–∏—è)
def get_default_rates():
    return {
        'USD': 83.6813,
        'EUR': 89.6553,
        'BYN': 27.34,
        'CNY': 11.46,
        'JPY': 0.55,
        'KRW': 0.05705,
        'RUB': 1
    }

# –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –∫—É—Ä—Å–æ–≤ –≤–∞–ª—é—Ç –ø—Ä–∏ –∑–∞–ø—É—Å–∫–µ
EXCHANGE_RATES = fetch_exchange_rates_cbr()

def ensure_path_and_file(file_path):
    os.makedirs(os.path.dirname(file_path), exist_ok=True)
    if not os.path.exists(file_path):
        with open(file_path, 'w', encoding='utf-8') as f:
            json.dump({}, f, ensure_ascii=False, indent=2)

def load_rastamozka_data():
    global rastamozka_data
    try:
        with open(RASTAMOZKA_JSON_PATH, 'r', encoding='utf-8') as file:
            rastamozka_data = json.load(file)
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Ñ–∞–π–ª–∞ rastamozka.json: {e}")

def load_user_history_rastamozka():
    global user_history
    try:
        if os.path.exists(USER_HISTORY_PATH):
            with open(USER_HISTORY_PATH, 'r', encoding='utf-8') as db_file:
                user_history = json.load(db_file)
        else:
            print(f"–§–∞–π–ª {USER_HISTORY_PATH} –Ω–µ –Ω–∞–π–¥–µ–Ω! –°–æ–∑–¥–∞–Ω–∏–µ –Ω–æ–≤–æ–≥–æ...")
            user_history = {}
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∏—Å—Ç–æ—Ä–∏–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π: {e}")
        user_history = {}

def save_user_history_rastamozka():
    try:
        with open(USER_HISTORY_PATH, 'w', encoding='utf-8') as db_file:
            json.dump(user_history, db_file, ensure_ascii=False, indent=2)
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ –∏—Å—Ç–æ—Ä–∏–∏: {e}")

ensure_path_and_file(RASTAMOZKA_JSON_PATH)
ensure_path_and_file(USER_HISTORY_PATH)
load_rastamozka_data()
load_user_history_rastamozka()

