def process_view_rastamozka_selection(message):
    if message.text == "–í –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é":
        return_to_menu(message)
        return
    if message.text == "–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ —Ä–∞—Å—Ç–∞–º–æ–∂–∫—É":
        view_rastamozka_calc(message, show_description=False)
        return

    chat_id = message.chat.id
    user_id = str(message.from_user.id)

    calculations = user_history.get(user_id, {}).get('calculations', [])
    if not calculations:
        bot.send_message(chat_id, "‚ùå –£ –≤–∞—Å –Ω–µ—Ç —Å–æ—Ö—Ä–∞–Ω–µ–Ω–Ω—ã—Ö —Ä–∞—Å—á–µ—Ç–æ–≤ —Ä–∞—Å—Ç–∞–º–æ–∂–∫–∏!")
        view_rastamozka_calc(message, show_description=False)
        return

    print(f"–í–≤–æ–¥ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è {user_id} (—Ä–∞—Å—Ç–∞–º–æ–∂–∫–∏, –ø—Ä–æ—Å–º–æ—Ç—Ä): {message.text}")  # –õ–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ

    try:
        indices = [int(num.strip()) - 1 for num in message.text.split(',')]
        valid_indices = []
        invalid_indices = []

        for index in indices:
            if 0 <= index < len(calculations):
                valid_indices.append(index)
            else:
                invalid_indices.append(index + 1)

        if not valid_indices and invalid_indices:
            # –í—Å–µ –Ω–æ–º–µ—Ä–∞ –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã, –∑–∞–ø—Ä–∞—à–∏–≤–∞–µ–º –ø–æ–≤—Ç–æ—Ä–Ω—ã–π –≤–≤–æ–¥
            markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
            markup.add('–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ —Ä–∞—Å—Ç–∞–º–æ–∂–∫—É')
            markup.add('–í –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é')
            msg = bot.send_message(
                chat_id,
                "‚ùå –ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –Ω–æ–º–µ—Ä! –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ —Ä–∞—Å—á–µ—Ç—ã –∏–∑ —Å–ø–∏—Å–∫–∞:",
                reply_markup=markup
            )
            bot.register_next_step_handler(msg, process_view_rastamozka_selection)
            return

        if invalid_indices:
            # –ï—Å—Ç—å –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–µ –Ω–æ–º–µ—Ä–∞, –Ω–æ –µ—Å—Ç—å –∏ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–µ
            invalid_str = ", ".join(map(str, invalid_indices))
            bot.send_message(chat_id, f"‚ùå –ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–µ –Ω–æ–º–µ—Ä–∞ `{invalid_str}`! –û–Ω–∏ –±—É–¥—É—Ç –ø—Ä–æ–ø—É—â–µ–Ω—ã...", parse_mode='Markdown')

        for index in valid_indices:
            calc = calculations[index]
            car_cost_rub = calc['car_cost_value'] * EXCHANGE_RATES.get(calc['car_cost_currency'], 1)
            customs_fee = calculate_customs_fee(car_cost_rub)
            customs_duty = calculate_customs_duty(car_cost_rub, calc['engine_volume'], calc['car_age'], 
                                                calc['engine_type'], calc['car_importer'])
            utilization_fee = calculate_utilization_fee(calc['engine_volume'], calc['engine_type'], 
                                                      calc['car_age'], calc['car_importer'])
            excise = calculate_excise(calc['engine_power_value'], calc['engine_type'], calc['car_importer'])
            nds = calculate_nds(car_cost_rub, customs_duty, excise, calc['car_importer'])

            result = (
                f"üìä *–†–∞—Å—á–µ—Ç —Ä–∞—Å—Ç–∞–º–æ–∂–∫–∏ ‚Ññ{index + 1}. {calc['timestamp']}*\n\n\n"
                f"*–í–∞—à–∏ –¥–∞–Ω–Ω—ã–µ:*\n\n"
                f"üöó –ò–º–ø–æ—Ä—Ç–µ—Ä: {calc['car_importer']}\n"
                f"üìÖ –í–æ–∑—Ä–∞—Å—Ç –∞–≤—Ç–æ: {calc['car_age']}\n"
                f"üîß –¢–∏–ø –¥–≤–∏–≥–∞—Ç–µ–ª—è: {calc['engine_type']}\n"
                f"üí™ –ú–æ—â–Ω–æ—Å—Ç—å: {calc['engine_power_value']} {calc['engine_power']}\n"
                f"üìè –û–±—ä–µ–º –¥–≤–∏–≥–∞—Ç–µ–ª—è: {calc['engine_volume']} —Å–º¬≥\n"
                f"üí∞ –°—Ç–æ–∏–º–æ—Å—Ç—å: {calc['car_cost_value']} {calc['car_cost_currency']}\n\n"
                f"*–†–∞—Å—á–µ—Ç:*\n\n"
                f"üõÉ –¢–∞–º–æ–∂–µ–Ω–Ω—ã–π —Å–±–æ—Ä: {customs_fee:.2f} ‚ÇΩ\n"
                f"üè¶ –¢–∞–º–æ–∂–µ–Ω–Ω–∞—è –ø–æ—à–ª–∏–Ω–∞: {customs_duty:.2f} ‚ÇΩ\n"
                f"‚ôªÔ∏è –£—Ç–∏–ª–∏–∑–∞—Ü–∏–æ–Ω–Ω—ã–π —Å–±–æ—Ä: {utilization_fee:.2f} ‚ÇΩ\n"
                f"üìà –ê–∫—Ü–∏–∑: {excise:.2f} ‚ÇΩ\n"
                f"ü´∞ –ù–î–°: {nds:.2f} ‚ÇΩ\n"
                f"üíµ –ò—Ç–æ–≥–æ: {(calc['total_cost'] - car_cost_rub):.2f} ‚ÇΩ\n"
                f"üí∞ –°—Ç–æ–∏–º–æ—Å—Ç—å –∞–≤—Ç–æ–º–æ–±–∏–ª—è + —Ä–∞—Å—Ç–∞–º–æ–∂–∫–∞: {calc['total_cost']:.2f} ‚ÇΩ"
            )
            bot.send_message(chat_id, result, parse_mode='Markdown')

        # –í–æ–∑–≤—Ä–∞—â–∞–µ–º—Å—è –≤ –º–µ–Ω—é –ø–æ—Å–ª–µ —É—Å–ø–µ—à–Ω–æ–π –æ–±—Ä–∞–±–æ—Ç–∫–∏
        view_rastamozka_calc(message, show_description=False)

    except ValueError:
        # –ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –≤–≤–æ–¥–∞, –∑–∞–ø—Ä–∞—à–∏–≤–∞–µ–º –ø–æ–≤—Ç–æ—Ä–Ω—ã–π –≤–≤–æ–¥
        markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
        markup.add('–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ —Ä–∞—Å—Ç–∞–º–æ–∂–∫—É')
        markup.add('–í –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é')
        msg = bot.send_message(
            chat_id,
            "‚ùå –ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –≤–≤–æ–¥! –í–≤–µ–¥–∏—Ç–µ —á–∏—Å–ª–∞ —á–µ—Ä–µ–∑ –∑–∞–ø—è—Ç—É—é:",
            reply_markup=markup
        )
        bot.register_next_step_handler(msg, process_view_rastamozka_selection)

