# Paths to files
NALOG_JSON_PATH = os.path.join('files', 'nalog.json')
USER_HISTORY_PATH = os.path.join('data base', 'calculators', 'nalog', 'nalog_users.json')
PERECHEN_AUTO_PATH = os.path.join('files', 'auto_10mln_rub_2025.json')  # –û–±–Ω–æ–≤–ª–µ–Ω–Ω—ã–π –ø—É—Ç—å
TRANSPORT_TAX_BASE_PATH = os.path.join('files', 'transport_tax_{year}.json')

# Global variables
nalog_data = {}
user_history = {}
user_data = {}
expensive_cars = []
tax_rates = {}
available_years = [2021, 2022, 2023, 2024, 2025]  # –°–ø–∏—Å–æ–∫ –¥–æ—Å—Ç—É–ø–Ω—ã—Ö –≥–æ–¥–æ–≤

# Ensure file paths exist and load data
def ensure_path_and_file(file_path):
    os.makedirs(os.path.dirname(file_path), exist_ok=True)
    if not os.path.exists(file_path):
        with open(file_path, 'w', encoding='utf-8') as f:
            json.dump({}, f, ensure_ascii=False, indent=2)

def load_nalog_data():
    global nalog_data
    try:
        with open(NALOG_JSON_PATH, 'r', encoding='utf-8') as file:
            nalog_data = json.load(file)
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Ñ–∞–π–ª–∞ nalog.json: {e}")

def load_user_history_nalog():
    global user_history
    try:
        if os.path.exists(USER_HISTORY_PATH):
            with open(USER_HISTORY_PATH, 'r', encoding='utf-8') as db_file:
                user_history = json.load(db_file)
        else:
            print(f"–§–∞–π–ª {USER_HISTORY_PATH} –Ω–µ –Ω–∞–π–¥–µ–Ω! –°–æ–∑–¥–∞–Ω–∏–µ –Ω–æ–≤–æ–≥–æ...")
            user_history = {}
            save_user_history_nalog()
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∏—Å—Ç–æ—Ä–∏–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π: {e}")
        user_history = {}

def save_user_history_nalog():
    try:
        with open(USER_HISTORY_PATH, 'w', encoding='utf-8') as db_file:
            json.dump(user_history, db_file, ensure_ascii=False, indent=2)
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ –∏—Å—Ç–æ—Ä–∏–∏: {e}")

def load_expensive_cars():
    global expensive_cars
    try:
        with open(PERECHEN_AUTO_PATH, 'r', encoding='utf-8') as file:
            data = json.load(file)  # –¢–µ–ø–µ—Ä—å data ‚Äî —ç—Ç–æ –º–∞—Å—Å–∏–≤
            car_data = []
            for section in data:
                cost_range = "10-15" if "10 –º–∏–ª–ª–∏–æ–Ω–æ–≤ –¥–æ 15 –º–∏–ª–ª–∏–æ–Ω–æ–≤ —Ä—É–±–ª–µ–π" in section['subtitle'] else "15+"
                for car in section['cars']:
                    car_data.append({
                        "brand": car['brand'],
                        "model": car['model'],
                        "engine_type": car['engine_type'],
                        "engine_volume": str(car['engine_displacement']) if car['engine_displacement'] is not None else "N/A",
                        "years_passed": car['age'],
                        "cost_range": cost_range
                    })
            expensive_cars = car_data
    except json.JSONDecodeError as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ —Ä–∞–∑–±–æ—Ä–µ JSON –≤ —Ñ–∞–π–ª–µ auto_10mln_rub_2025.json: {e}")
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Ñ–∞–π–ª–∞ auto_10mln_rub_2025.json: {e}")

def load_tax_rates(year):
    global tax_rates
    try:
        tax_file_path = TRANSPORT_TAX_BASE_PATH.format(year=year)
        if not os.path.exists(tax_file_path):
            raise FileNotFoundError(f"–§–∞–π–ª {tax_file_path} –Ω–µ –Ω–∞–π–¥–µ–Ω!")
        with open(tax_file_path, 'r', encoding='utf-8') as file:
            tax_rates = json.load(file)
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Ñ–∞–π–ª–∞ transport_tax_{year}.json: {e}")
        tax_rates = {}

# –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –¥–∞–Ω–Ω—ã—Ö
ensure_path_and_file(NALOG_JSON_PATH)
ensure_path_and_file(USER_HISTORY_PATH)
load_nalog_data()
