            markup = types.ReplyKeyboardMarkup(row_width=1, resize_keyboard=True)
            markup.add('–û—Ç–æ–∑–≤–∞—Ç—å —Ä–µ–∫–ª–∞–º—É')
            markup.add('–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –º–µ–Ω—é –¥–ª—è —Ä–µ–∫–ª–∞–º—ã')
