def process_view_points(message):
    if message.text == "–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ —É–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –±–∞–ª–ª–∞–º–∏":
        manage_points(message)
        return
    if message.text == "–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ —É–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Å–∏—Å—Ç–µ–º–æ–π":
        manage_system(message)
        return
    if message.text == "–í –º–µ–Ω—é –∞–¥–º–∏–Ω-–ø–∞–Ω–µ–ª–∏":
        show_admin_panel(message)
        return

    user_input = message.text.strip()
    user_id = None

    users_data = load_users()
    if user_input.isdigit():
        if len(user_input) >= 5:
            user_id = user_input
        else:
            idx = int(user_input)
            if 1 <= idx <= len(users_data):
                user_id = list(users_data.keys())[idx - 1]
    elif user_input.startswith('@'):
        username = user_input[1:]
        for user_id, data in users_data.items():
            if data['username'] == username:
                break
        else:
            user_id = None

    if not user_id:
        bot.send_message(message.chat.id, "–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –Ω–∞–π–¥–µ–Ω! –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞", parse_mode="Markdown")
        bot.register_next_step_handler(message, process_view_points)
        return

    data = load_payment_data()
    user_data = data['subscriptions']['users'].get(str(user_id), {})
    points = user_data.get('referral_points', 0)
    # –ò—Å–ø—Ä–∞–≤–ª–µ–Ω–æ: —É–±—Ä–∞–Ω–æ escape_markdown –¥–ª—è username
    username = users_data.get(str(user_id), {}).get('username', f"@{user_id}")

    history = user_data.get('points_history', [])
    total_earned = sum(entry['points'] for entry in history if entry['action'] == 'earned')
    total_spent = sum(entry['points'] for entry in history if entry['action'] == 'spent')
    earned_referrals = sum(entry['points'] for entry in history if entry['action'] == 'earned' and entry.get('source') == 'referral')
    earned_purchases = sum(entry['points'] for entry in history if entry['action'] == 'earned' and entry.get('source') == 'purchase')
    earned_top_referrals = sum(entry['points'] for entry in history if entry['action'] == 'earned' and entry.get('source') == 'top_referral')
    earned_gifts = sum(entry['points'] for entry in history if entry['action'] == 'earned' and entry.get('source') == 'gift')
    spent_time = sum(entry['points'] for entry in history if entry['action'] == 'spent' and entry.get('reason') == '–û–±–º–µ–Ω –Ω–∞ –≤—Ä–µ–º—è')
    spent_discounts = sum(entry['points'] for entry in history if entry['action'] == 'spent' and entry.get('reason') == '–û–±–º–µ–Ω –Ω–∞ —Å–∫–∏–¥–∫—É')
    spent_features = sum(entry['points'] for entry in history if entry['action'] == 'spent' and entry.get('reason') == '–û–±–º–µ–Ω –Ω–∞ —Ñ—É–Ω–∫—Ü–∏–∏')
    gifted_points = sum(entry['points'] for entry in history if entry['action'] == 'spent' and entry.get('reason') == '–ü–æ–¥–∞—Ä–æ–∫ –¥—Ä—É–≥–æ–º—É –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—é')

    points_summary = (
        f"üí∞ *–ë–∞–ª–ª—ã –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è {username} - `{user_id}`:*\n\n"
        f"üéÅ *–¢–µ–∫—É—â–∏–µ –±–∞–ª–ª—ã:* {points}\n\n"
        f"üî• *–í—Å–µ–≥–æ –∑–∞—Ä–∞–±–æ—Ç–∞–Ω–æ:* {total_earned}\n"
        f"  ‚Ä¢ –ó–∞ —Ä–µ—Ñ–µ—Ä–∞–ª–æ–≤: {earned_referrals}\n"
        f"  ‚Ä¢ –ó–∞ –ø–æ–∫—É–ø–∫–∏: {earned_purchases}\n"
        f"  ‚Ä¢ –ó–∞ —Ç–æ–ø —Ä–µ—Ñ–µ—Ä–∞–ª–æ–≤: {earned_top_referrals}\n"
        f"  ‚Ä¢ –ü–æ–ª—É—á–µ–Ω–æ –≤ –ø–æ–¥–∞—Ä–æ–∫: {earned_gifts}\n\n"
        f"üí∏ *–í—Å–µ–≥–æ –ø–æ—Ç—Ä–∞—á–µ–Ω–æ:* {total_spent}\n"
        f"  ‚Ä¢ –ù–∞ –≤—Ä–µ–º—è: {spent_time}\n"
        f"  ‚Ä¢ –ù–∞ —Å–∫–∏–¥–∫–∏: {spent_discounts}\n"
        f"  ‚Ä¢ –ù–∞ —Ñ—É–Ω–∫—Ü–∏–∏: {spent_features}\n"
        f"  ‚Ä¢ –ü–æ–¥–∞—Ä–µ–Ω–æ –¥—Ä—É–≥–∏–º: {gifted_points}\n\n"
        f"‚è≥ *–ü–æ–¥–∞—Ä–µ–Ω–æ –≤—Ä–µ–º–µ–Ω–∏:* 0 –º–∏–Ω.\n"
        f"‚è≥ *–ü–æ–ª—É—á–µ–Ω–æ –≤—Ä–µ–º–µ–Ω–∏:* 0 –º–∏–Ω."
    )

    message_parts = split_message(points_summary)
    for part in message_parts:
        bot.send_message(message.chat.id, part, parse_mode="Markdown")

    manage_points(message)

